import React from 'react'
import { useState } from 'react';

function App() {
  let arr=[]
  let [text,setText]=useState();
  let [newText,setNewText]=useState();

  const changeText=(event)=>{
    setText(event.target.value)

  }

  const addItem =()=>{
    setNewText(text)
    arr.push(newText)
  }
  console.arr
  return (
    <>

        <input type="text" placeholder='add your list' value={text} onChange={changeText} />
        <button onClick={addItem}>Add</button>

        <h3>{arr.map((item)=>{
            return item;
        })}</h3>
    </>
  );
}

export default App;
