import React from 'react'
import { useState } from 'react';

function App() {
  let arr=[]
  let [text,setText]=useState();
  let [newText,setNewText]=useState();

  const changeText=(event)=>{
    setText(event.target.value)

  }

  const addItem =(event)=>{
    let text1=setNewText(text)
    arr.push(text1)
  }

  return (
    <>
      <form onSubmit={addItem}>
        <input type="text" placeholder='add your list' value={text} onChange={changeText} />
        <button>Add</button>
      </form>
      <h3>{arr[arr]}</h3>
    </>
  );
}

export default App;
